                if ("createEvent" in document) {
                    event = document.createEvent("Event");
                    event.initEvent(eventType, bubbles, true);
                } else if ("Event" in window) {
                    event = new Event(eventType, {
                        bubbles: bubbles,
                        cancelable: true
                    });
                }

                element.focus();
                element.dispatchEvent(event);
            }
        }
    }, 0);
