<script>
	// Exit Capture Timer
	// Setup vars
	var hr;
	var mn;
	var sc;
	var thr;
	var tmn;
	var tsc;

	// Set time for hours, mins and secs
	hr = Number('0');
	mn = Number('15');
	sc = Number('0');

	// If no time is set, provide a default of 0
	if(hr  == ""){
		hr = 0;
	}
	if(mn  == ""){
		mn = 0;
	}
	if(sc  == ""){
		sc = 0;
	}

	// Ensure vars are intervals 
	parseInt(hr);
	parseInt(mn);
	parseInt(sc);

	// Configure vars into time
	hr = hr*3600;
	mn = mn*60
	sc = hr + mn + sc;

	// Init timer
	start(sc);


	// Run timer
	function start(e){
		var interv = setInterval(function(){
			// Hours
			thr = e/3600;
			thr = Math.floor(thr);
			if(thr < 10){thr = "0" + thr;}
			jQuery('#timerhr').html(thr);

			// Mins
			tmn = (e%3600)/60;
			tmn = Math.floor(tmn);
			if(tmn < 10){tmn = "0" + tmn;}
			jQuery('#timermn').html(tmn);

			// Sec
			tsc = e-(thr*3600+tmn*60);
			if(tsc < 10){tsc = "0" + tsc;}
			jQuery('#timersc').html(tsc);
			e--;


			// Timer Expired
			if(e < 0){
				clearInterval(interv);
				jQuery('#ltkpopup-content #ltkpopup-timer-duration').remove();

			}

		},1000);
	}

</script>

